package cvijic.nina;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		// Operatori inkrementiranja i dekrementiranja
		
		int i = 3;
		
		i+=1; // primena operatora inkrementiranja
		System.out.println(i);
		
		/*
		 *  i++
		 *  i + = 1
		 *  i = i + 1
		 */
		
		// Operator dekrementiranja (umanjenja vrednosti za 1)
		
		int b = 20;
		b--; // -- predstavljaju operator dekrementiranja
	 //   b = b - 1
		System.out.println(b);
		
		float c = 20.56f;  // sa decimalnim brojevima (zarezom)
		c--;
		System.out.println(c);
		
	}

}
